# The views used below are normally mapped in the AdminSite instance.
# This URLs file is used to provide a reliable view deployment for test purposes.
# It is also provided as a convenience to those who want to deploy these URLs
# elsewhere.

from django.contrib.auth import views
from django.urls import path

urlpatterns = [
    # VISTAS PÚBLICAS
    path('', views.ListaVuelosView.as_view(), name='vuelos_disponibles'),

    # AUTENTICACIÓN
    path('login/', views.login_view, name='login'),
    path('registro/', views.registro_view, name='registro'),

    # CRUD ADMIN
    path('admin/vuelos/', views.VueloAdminListView.as_view(), name='vuelos_admin_list'),
    path('admin/vuelos/nuevo/', views.VueloCreateView.as_view(), name='vuelo_crear'),
    path('admin/vuelos/<int:pk>/editar/', views.VueloUpdateView.as_view(), name='vuelo_editar'),
    path('admin/vuelos/<int:pk>/eliminar/', views.VueloDeleteView.as_view(), name='vuelo_eliminar'),

    # PASAJEROS / RESERVAS
    path('vuelos/<int:pk>/', views.detalle_vuelo_view, name='detalle_vuelo'),
    path('vuelos/<int:vuelo_pk>/reservar/', views.crear_reserva_view, name='crear_reserva'),
    path('reservas/<int:reserva_pk>/asientos/', views.seleccionar_asiento_view, name='seleccionar_asiento'),
    path('reservas/<int:pk>/detalle/', views.reserva_detalle_view, name='reserva_detalle'),
]